func replaceNonCoprimes(a []int) []int {
	gcd := func(a, b int) int {
		for a != 0 {
			a, b = b%a, a
		}
		return b
	}

	st := []int{1}
	for _, v := range a {
		st = append(st, v)
		for i := len(st) - 2; i >= 0; i-- {
			x := gcd(st[i], st[i+1])
			if x > 1 {
				st[i] = st[i] * st[i+1] / x
				st = st[:len(st)-1]
			} else {
				break
			}
		}
	}
	return st[1:]
}
